      const _update = () => {
        this.actualWidth += diff;
        console.log('OWOW', this.actualWidth);
        if ((diff < 0) && (this.actualWidth > this.closeWidth) && this.isHover) window.requestAnimationFrame(_update);
        else if ((diff > 0) && (this.actualWidth < this.openWidth) && !this.isHover) window.requestAnimationFrame(_update);
      }
      _update();
      // this.actualWidth += diff;
    },
    onOpen() {
      if (this.dontOpen) return;
      this.isHover = true;
      // this.updateWidth(1);
      this.actualWidth = this.openWidth;
      // while (this.actualWidth < this.openWidth) this.actualWidth +=1;
    },
    onClose() {
      if (this.dontOpen) return;
      this.isHover = false;
      // // this.updateWidth(-1);
      this.actualWidth = this.closeWidth;
      // // while (this.actualWidth > this.openWidth) this.actualWidth -=1;
    }
  }
